import React from 'react'
import {connect} from 'react-redux'
// import {Link} from 'react-router-dom'
import {editTodo} from '../actions/todoToEditAction.js'
import {deleteTodo, markComplete} from '../actions/todoActions'
import {useHistory, useLocation} from 'react-router'

const Todo = ({todos, deleteTodo, editTodo, markComplete}) => {

    console.log(state.todos)

    const history = useHistory()
    const location = useLocation()
    const todo = this.props.todos.filter(todo => todo.id === window.location.href.split('/')[3])

    const handleDelete = () => {
        deleteTodo(todo.id)
    }

    const handleComplete = () => {
        markComplete(todo)
    }

    const handleEdit = (todo) => {
        history.push(`/edit/${todo.id}`)
        editTodo(todo)
    }

    return (
        <div>
            <div>
                <h3>{todo.title}</h3>

                <p style={{textDecoration: todo.completed ? 
                'line-through' : 'none'}}>{todo.content}</p>

                {todo.title ?
                <div>
                    {todo.completed === false ?
                <div>
                <button onClick={handleComplete}>Mark As Complete</button>
                <button onClick={() => handleEdit(todo)}>Edit</button>
                </div>
                :null}
                <button onClick={handleDelete}>Delete</button>
                </div>
                : null}
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    todos: state.todos
})

export default connect(mapStateToProps, {deleteTodo, editTodo, markComplete})(Todo)
