import React from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import {deleteTodo, markComplete} from '../actions/todoActions'

const Todo = ({todo, deleteTodo, markComplete, handleEdit}) => {

    const handleDelete = () => {
        deleteTodo(todo.id)
    }

    const handleComplete = () => {
        markComplete(todo)
    }

    return (
        <div>
            <div>
                <h3>{todo.title}</h3>

                <p style={{textDecoration: todo.completed ? 
                'line-through' : 'none'}}>{todo.content}</p>

                {todo.title ?
                <div>
                    {todo.completed === false ?
                <span>
                <Link onClick={handleComplete} to='/'>Mark As Complete</Link>          
                {/* <a onClick={handleComplete}>Mark As Complete</a> */}
                <Link onClick={() => handleEdit(todo)} to='/new'>Edit</Link>
                {/* <button onClick={() => handleEdit(todo)}>Edit</button> */}
                </span>
                :null}
                <button onClick={handleDelete}>Delete</button>
                </div>
                : null}
            </div>
        </div>
    )
}

// const mapDispatchToProps = {
//     deleteTodo: deleteTodo
// }

export default connect(null, {deleteTodo, markComplete})(Todo)
