export default function todosReducer(state = [], action) {
    switch (action.type) {
      case "FETCH_TODOS":
        return action.todos
      case "NEW_TODO":
        return [...state, action.todo]
      case "DELETE_TODO":
        const updatedTodos = state.filter(t => t.id !== action.id)
        return updatedTodos
      default:
        return state
      }
  }
