const BASE_URL = 'http://localhost:3000'
const TODOS_URL = `${BASE_URL}/todos`
const EDIT_TODO = 'EDIT_TODO'

export const editTodo = (todo) => dispatch => {
    fetch(TODOS_URL,{
        method: 'POST',
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(todo)
    })
    .then(res => res.json())
    .then(todo =>   dispatch({
        type: NEW_TODO,
        todo
    }))
}
    //pass todo to edit form and set state
    //patch request using todo id, return todo
    //update store state
