import React from 'react'
import {connect} from 'react-redux'
// import {Link} from 'react-router-dom'
import {editTodo} from '../actions/todoToEditAction.js'
import {deleteTodo, markComplete} from '../actions/todoActions'
import {useHistory} from 'react-router'

import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';


const Todo = ({todo, deleteTodo, editTodo, markComplete}) => {

    const history = useHistory()

    const handleDelete = () => {
        deleteTodo(todo.id)
    }

    const handleComplete = () => {
        markComplete(todo)
    }

    const handleEdit = (todo) => {
        history.push(`/edit/${todo.id}`)
        editTodo(todo)
    }

    const useStyles = makeStyles({
        root: {
          minWidth: 275,
        },
        title: {
          fontSize: 14,
        },
        pos: {
          marginBottom: 12,
        },
      })

      function CardStyled() {
          const classes = useStyles();
          return <Card classname={classes.root}>
              <CardContent>
                <Typography className={classes.title} color="textSecondary" gutterBottom> 
                <h3>{todo.title}</h3>
                </Typography>
                <Typography className={classes.pos} color="textSecondary">
                <p style={{textDecoration: todo.completed ? 
                'line-through' : 'none'}}>{todo.content}</p>
                </Typography>
            </CardContent>
                {todo.title ?
                <div>
                    {todo.completed === false ?
                <div>
                <Button style={{margin:10}} variant="outlined" size='small' onClick={handleComplete}>Mark As Complete</Button>
                <Button style={{margin:10}} variant="outlined" size='small' onClick={() => handleEdit(todo)}>Edit</Button>
                <Button style={{margin:10}} variant="outlined" size='small' onClick={handleDelete}>Delete</Button>
                </div>
                :<Button style={{margin:10}} variant="outlined" size='small' onClick={handleDelete}>Delete</Button>}
                </div>
                : null}
          </Card>
      }



    return (
        <div>
            <div>
                <CardStyled variant="outlined" elevation={3}/>
            </div>
        </div>
    )
}

export default connect(null, {deleteTodo, editTodo, markComplete})(Todo)
