        }
    }

    componentDidMount() {
        this.props.fetchTodos()
    }

    showCompleted = () => {
        this.props.todos.filter(todo => todo.completed === true)
    }

    render() {

        return (
            <div>
                <button onClick={this.showCompleted}>Show Completed</button>
                <TodoIndex todos={this.props.todos} handleEdit={this.handleEdit}/>
            </div>
        )

    }
}

const mapStateToProps = state => ({
    todos: state.todos
})

export default connect(mapStateToProps, {fetchTodos})(MainContainer)
