export default function todoToEditReducer(state = {title: ''}, action) {

  let todoToEdit;

    switch (action.type) {
        case "EDIT_TODO":
          todoToEdit = {
            title: action.todoToEdit.title,
            content: action.todoToEdit.content
          }
            return todoToEdit
      default:
        return state
      }
  }
