import React from 'react'
import {connect} from 'react-redux'
import {deleteTodo, markComplete} from '../actions/todoActions'

const Todo = ({todo, deleteTodo, markComplete}) => {

    const handleDelete = () => {
        deleteTodo(todo.id)
    }

    const handleComplete = () => {
        const reqObj = {
            method: 'PATCH',
            headers: {
                'content-type': 'applcation/json'
            }, 
            body: JSON.stringify({
                completed: true
            })
        }

        fetch(`http://localhost:3000/todos/${todo.id}`, reqObj)
        .then(res => res.json())
        .then(data )
        markComplete(todo)
    }

    return (
        <div>
            <div key={todo.id}>
                <h3>{todo.title}</h3>

                <p style={{textDecoration: todo.completed ? 
                'line-through' : 'none'}}>{todo.content}</p>

                <button onClick={handleComplete}>Mark As Complete</button>
                <button>Edit</button>
                <button onClick={handleDelete}>Delete</button>
            </div>
        </div>
    )
}

// const mapDispatchToProps = {
//     deleteTodo: deleteTodo
// }

export default connect(null, {deleteTodo, markComplete})(Todo)
