export default function todosReducer(state = [], action) {
  let updatedTodos
  switch (action.type) {
      case "FETCH_TODOS":
        return action.todos
      case "NEW_TODO":
        return [...state, action.todo]
      case "DELETE_TODO":
        updatedTodos = state.filter(t => t.id !== action.todo.id)
        return updatedTodos
      case "COMPLETE_TODO":
        console.log(action.payload)
        updatedTodos = state.map(t => {
          if(t.id === action.updatedTodoId){
            return state.find(action.updatedTodoId)
          }
          else{
            return t
          }
        })
        return updatedTodos
      default:
        return state
      }
  }
