const BASE_URL = 'http://localhost:3000'
const TODOS_URL = `${BASE_URL}/todos`
const EDIT_TODO = 'EDIT_TODO'
REVERT_TODO = 'REVERT_TODO'

export const editTodo = (todoToEdit) => {
    return {
        type: EDIT_TODO,
        todoToEdit
    }
}

export const revertTodoToEdit = (todoToEdit) => {
    return {
        type: REVERT_TODO,
        todoToEdit
    }
}
