export default function todoToEditReducer(state = null, action) {

  let todoToEdit;

    switch (action.type) {
        case "EDIT_TODO":
          return action.todoToEdit
        case "REVERT_TODO":
          todoToEdit = null
          return todoToEdit
      default:
        return state
      }
  }
