import React from 'react'
import {connect} from 'react-redux'
// import {Link} from 'react-router-dom'
import {editTodo} from '../actions/todoToEditAction.js'
import {deleteTodo, markComplete} from '../actions/todoActions'
import {useHistory} from 'react-router'
import { Button } from '@material-ui/core';


const Todo = ({todo, deleteTodo, editTodo, markComplete}) => {

    const history = useHistory()

    const handleDelete = () => {
        deleteTodo(todo.id)
    }

    const handleComplete = () => {
        markComplete(todo)
    }

    const handleEdit = (todo) => {
        history.push(`/edit/${todo.id}`)
        editTodo(todo)
    }

    return (
        <div>
            <div>
                <h3>{todo.title}</h3>

                <p style={{textDecoration: todo.completed ? 
                'line-through' : 'none'}}>{todo.content}</p>

                {todo.title ?
                <div>
                    {todo.completed === false ?
                <div>
                <Button variant="contained" size='small' onClick={handleComplete}>Mark As Complete</Button><br/>
                <Button variant="contained" size='small' onClick={() => handleEdit(todo)}>Edit</Button><br/>
                </div>
                :null}
                <Button variant="contained" size='small' onClick={handleDelete}>Delete</Button>
                </div>
                : null}
            </div>
        </div>
    )
}

export default connect(null, {deleteTodo, editTodo, markComplete})(Todo)
