import React, { Component } from 'react'
import {connect} from 'react-redux'
import {fetchTodos} from '../actions/todoActions'

import TodoIndex from '/Users/lauren/Flatiron/note-taker-fe/src/components/TodoIndex.js'
import EditTodoForm from '../components/EditTodoForm'

class MainContainer extends Component {



    componentDidMount() {
        this.props.fetchTodos()
    }

    handleEdit = (todo) => {
        <EditTodoForm todoToEdit={todo} />
        // window.history.pushState({page: 'edit'}, "", `/edit/${todo.id}`)
    }



    render() {

        return (
            <div>
                <TodoIndex key={this.props.todos.id} todos={this.props.todos} handleEdit={this.handleEdit}/>
            </div>
        )

    }
}

const mapStateToProps = state => ({
    todos: state.todos
})

export default connect(mapStateToProps, {fetchTodos})(MainContainer)
