export default function todosReducer(state = [], action) {
  let updatedTodos
  let todo
  switch (action.type) {
      case "FETCH_TODOS":
        return action.todos
      case "NEW_TODO":
        return [...state, action.todo]
      case "DELETE_TODO":
        updatedTodos = state.filter(t => t.id !== action.todo.id)
        return updatedTodos
      case "COMPLETE_TODO":
        // console.log(action.todo.id)
        // todo = state.find(action.todo.id)
        updatedTodos = state.map(t => {
          if(t.id === action.todo.id){
            return action.todo
          }
          else{
            return t
          }
        })
        // console.log(updatedTodos)
        return updatedTodos
      default:
        return state
      }
  }
