const GET_TODO = 'GET_TODO'

export const getTodo = (todoToShow) => dispatch =>  { 
        dispatch({
            type: GET_TODO,
            todoToShow
        })
}

export const fetchTodoToShow = (todoId) => {
    console.log(action)
    fetch(`http://localhost:3000/todos/${todoId}`)
    .then(res => res.json())
    .then(todo => 
        getTodo(todo)
    )
}
