import React from 'react'
import {connect} from 'react-redux'
// import {Link} from 'react-router-dom'
import {editTodo} from '../actions/todoToEditAction.js'
import {deleteTodo, markComplete} from '../actions/todoActions'
import {useHistory} from 'react-router'

import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';



const Todo = ({todo, deleteTodo, editTodo, markComplete}) => {

    const history = useHistory()

    const handleDelete = () => {
        deleteTodo(todo.id)
    }

    const handleComplete = () => {
        markComplete(todo)
    }

    const handleEdit = (todo) => {
        history.push(`/edit/${todo.id}`)
        editTodo(todo)
    }

    const SimpleMenu = () => {
        const [anchorEl, setAnchorEl] = React.useState(null);

        const handleClick = (event) => {
          setAnchorEl(event.currentTarget);
        };

        const handleClose = () => {
          setAnchorEl(null);
        };
    }

    return (
        <div>
            <div>
                <h3>{todo.title}</h3>

                <p style={{textDecoration: todo.completed ? 
                'line-through' : 'none'}}>{todo.content}</p>

            <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
            Open Menu
            </Button>
            <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
            >
            <MenuItem onClick={handleClose}>Profile</MenuItem>
            <MenuItem onClick={handleClose}>My account</MenuItem>
            <MenuItem onClick={handleClose}>Logout</MenuItem>
            </Menu>

                {todo.title ?
                <div>
                    {todo.completed === false ?
                <div>
                <Button style={{margin:10}} variant="outlined" size='small' onClick={handleComplete}>Mark As Complete</Button>
                <Button style={{margin:10}} variant="outlined" size='small' onClick={() => handleEdit(todo)}>Edit</Button>
                <Button style={{margin:10}} variant="outlined" size='small' onClick={handleDelete}>Delete</Button>
                </div>
                :<Button style={{margin:10}} variant="outlined" size='small' onClick={handleDelete}>Delete</Button>}
                </div>
                : null}
            </div>
        </div>
    )
}

export default connect(null, {deleteTodo, editTodo, markComplete})(Todo)
