function bookReducer(state = [], action) {
    let idx;
    switch (action.type) {
      case "ADD_BOOK":
        return {
          books: [...state.books, action.book]
        };

      case "REMOVE_BOOK":
        idx = state.books.findIndex(book => book.id === action.id);
        return {
          books: [...state.books.slice(0, idx), ...state.books.slice(idx + 1)]
        };
      default:
        return state
      }
  }
