import React, { Component } from 'react'
import {connect} from 'react-redux'
import {editTodo} from '/Users/lauren/Flatiron/note-taker-fe/src/actions/todoToEditAction.js'

class EditTodoForm extends Component {
    constructor(props){
        console.log(props)
        super(props)
        this.state = {
            title: '',
            content: ''
        }
    }

    componentDidUpdate(prevProps, prevState){
        if (this.props.todoToEdit && prevState.title === '') {
            this.setState({
                title: this.props.todoToEdit.title,
                content: this.props.todoToEdit.content
            })
        }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    // handleSubmit = (event) => {
    //     event.preventDefault()
    //     const todo = {
    //         title: this.state.title,
    //         content: this.state.content
    //     }
    //     this.setState({
    //         title: '',
    //         content: ''
    //     })
    // }

    //remember to clear


    render() {

        // console.log(this.state)

        return (
            <div>
                <h3>Edit Note</h3>
                <form onSubmit={this.handleSubmit}>
                    <label>Title</label>
                    <input onChange={this.handleChange} type="text" name="title" value={this.state.title}/>
                    <label>Content</label>
                    <input onChange={this.handleChange} type="text" name="content" value={this.state.content}/>
                    <button>Submit!</button>
                </form>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {todoToEdit: state.todoToEdit}
}

export default connect(mapStateToProps)(EditTodoForm)
