import React, { Component } from 'react'
import {connect} from 'react-redux'
import {fetchTodos} from '../actions/todoActions'
import TodoIndex from '/Users/lauren/Flatiron/note-taker-fe/src/components/TodoIndex.js'

let toggle = true

class MainContainer extends Component {
    constructor(props){
        super(props)
        this.state = {
            todos: []
        }
    }

    componentDidMount() {
        this.props.fetchTodos()
    }

    showAll = () => {
        this.setState({
            todos: this.props.todos
        })
    }

    sortByCompleted = () => {
        toggle = !toggle
        console.log(toggle)
        if (toggle === false){
            this.setState({
                todos: this.props.todos.filter(todo => todo.completed === true)
            })
        }
        else {
            this.setState({
                todos: this.props.todos.filter(todo => todo.completed === false)
            })
        }
    }

    render() {

        return (
            <div >
                <button style={{position: 'absolute', left: '0'}} onClick={this.showAll}>Show All</button>
                <button style={{position: 'absolute', right: '0'}} onClick={this.showCompleted}>Show Completed</button>
                <TodoIndex todos={this.state.todos} handleEdit={this.handleEdit}/>
            </div>
        )

    }
}

const mapStateToProps = state => ({
    todos: state.todos
})

export default connect(mapStateToProps, {fetchTodos})(MainContainer)
