const BASE_URL = 'http://localhost:3000'
const TODOS_URL = `${BASE_URL}/todos`
const EDIT_TODO = 'EDIT_TODO'

export const editTodo = (todo) => dispatch => {
    dispatch({
        type: EDIT_TODO,
        todo
    })
}
